  getTwoDayItineraryForDay(day: number) {
    return this.itineraries.twoDays.filter((item) => item.day === day);
  }

  getThreeDayItineraryForDay(day: number) {
    return this.itineraries.threeDays.filter((item) => item.day === day);
  }

  handleImageError(event: any) {
    event.target.src =
      'data:image/jpeg;base64,';
  }

  getActivityStatusClass(type: string) {
    return {
      'status-hussein': type === 'hussein-tour',
      'status-suggestion': type === 'suggestion',
    };
  }

  groupActivitiesByDay(activities: any[]) {
    return activities.reduce((groups: any, activity) => {
      const day = activity.day || 1;
      if (!groups[day]) {
        groups[day] = [];
      }
      groups[day].push(activity);
      return groups;
    }, {});
  }

